using Amazon.Polly;
using Amazon.Polly.Model;
using System;
using System.Collections.Concurrent;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using TustlerInterfaces;

namespace TustlerAWSLib.Mocks
{
    public class MockPolly :IAmazonWebInterfacePolly
    {
        private readonly AmazonWebServiceInterface awsInterface;
        private readonly string notificationsARN;

        private readonly Dictionary<VoiceId, Voice> voiceDictionary;
        private readonly Dictionary<string, LexiconDescription> lexiconDictionary;          // keyed by lexiconName
        private readonly ConcurrentDictionary<string, SynthesisTask> speechSynthesisTaskDictionary;   // keyed by TaskId

        private const string audioUUencoded = @"









































";

        public MockPolly(AmazonWebServiceInterface awsInterface, string notificationsARN)
        {
            this.awsInterface = awsInterface;
            this.notificationsARN = notificationsARN;

            var voices = new Voice[]
            {
                new Voice() {
                    Id="Lotte", Name="Lotte", Gender=Gender.Female,
                    LanguageCode="nl-NL", LanguageName="Dutch", AdditionalLanguageCodes=new List<string>(),
                    SupportedEngines= new List<string>() { "Neural" }
                },
                new Voice() {
                    Id="Marlene", Name="Marlene", Gender=Gender.Female,
                    LanguageCode="de-DE", LanguageName="German", AdditionalLanguageCodes=new List<string>(),
                    SupportedEngines= new List<string>() { "Neural" }
                },
                new Voice() {
                    Id="Joanna", Name="Joanna", Gender=Gender.Female,
                    LanguageCode="en-US", LanguageName="US English", AdditionalLanguageCodes=new List<string>(),
                    SupportedEngines= new List<string>() { "Neural", "Standard" }
                }
            };

            voiceDictionary = new Dictionary<VoiceId, Voice>(voices.Select(v => KeyValuePair.Create(v.Id, v)));

            var lexicons = new LexiconDescription[] {
                new LexiconDescription()
                {
                    Name="colour",
                    Attributes= new LexiconAttributes()
                    {
                        LexiconArn = "arn:aws:polly:ap-southeast-2:261914005867:lexicon/colour",
                        LanguageCode = "en-US",
                        Alphabet = "ipa",
                        LastModified = DateTime.Now,
                        LexemesCount = 1,
                        Size = 646
                    }
                },
                new LexiconDescription()
                {
                    Name="judgement",
                    Attributes= new LexiconAttributes()
                    {
                        LexiconArn = "arn:aws:polly:ap-southeast-2:261914005867:lexicon/judgement",
                        LanguageCode = "en-US",
                        Alphabet = "ipa",
                        LastModified = DateTime.Now,
                        LexemesCount = 2,
                        Size = 953
                    }
                }
            };

            lexiconDictionary = new Dictionary<string, LexiconDescription>(lexicons.Select(ld => KeyValuePair.Create(ld.Name, ld)));
            speechSynthesisTaskDictionary = new ConcurrentDictionary<string, SynthesisTask>();
        }

        public async Task<AWSResult<List<Voice>>> DescribeVoices(string languageCode)
        {
            await Task.Delay(1000);

            var voices = voiceDictionary.Where(kvp => languageCode is null? true : kvp.Value.LanguageCode == languageCode).Select(kvp => kvp.Value);
            return await Task.FromResult(new AWSResult<List<Voice>>(new List<Voice>(voices), null));
        }

        public async Task<AWSResult<LexiconAttributes>> GetLexicon(string lexiconName)
        {
            await Task.Delay(1000);

            if (lexiconDictionary.ContainsKey(lexiconName))
            {
                var attributes = lexiconDictionary[lexiconName].Attributes;
                return await Task.FromResult(new AWSResult<LexiconAttributes>(attributes, null));
            }
            else
            {
                return await Task.FromResult(new AWSResult<LexiconAttributes>(null, new AWSException("Mock GetLexicon", $"The key {lexiconName} does not exist", new KeyNotFoundException("Key not found"))));
            }
        }

        public async Task<AWSResult<List<LexiconDescription>>> ListLexicons()
        {
            await Task.Delay(1000);

            var lexicons = new List<LexiconDescription>(lexiconDictionary.Values);
            return await Task.FromResult(new AWSResult<List<LexiconDescription>>(lexicons, null));
        }

        public async Task<AWSResult<PollyAudioStream>> SynthesizeSpeech(string text, Engine engine, string voiceId)
        {
            await Task.Delay(1000);

            var bytes = Convert.FromBase64String(audioUUencoded);
            MemoryStream audioStream = new MemoryStream(bytes, 0, bytes.Length, false, true);

            var contentType = "audio/mpeg";

            return await Task.FromResult(new AWSResult<PollyAudioStream>(new PollyAudioStream(audioStream, contentType), null));
        }

        public async Task<AWSResult<SynthesisTask>> StartSpeechSynthesisTask(string bucketName, string key, string arn, string text, Engine engine, string voiceId)
        {
            await Task.Delay(1000);

            var taskId = $"SpeechTask-{DateTime.Now.Ticks}";    // actually something like: 7bed85f0-b4fa-4129-a4c0-8378cfeb2936
            var languageCode = voiceDictionary?[voiceId]?.LanguageCode;
            var lexiconNames = new List<string>(lexiconDictionary.Keys);

            var synthesisTask = new SynthesisTask()
            {
                CreationTime = DateTime.Now,
                Engine = engine,
                LanguageCode = languageCode,            // can be null
                LexiconNames = lexiconNames,
                OutputFormat = "mp3",
                OutputUri = "https://s3.ap-southeast-2.amazonaws.com/tator/SpeechTaskOutput-637231734588699685.7bed85f0-b4fa-4129-a4c0-8378cfeb2936.mp3",
                RequestCharacters = 45,
                SampleRate = "24000",                   // can be null
                SnsTopicArn = arn,                      // should be: "arn:aws:sns:ap-southeast-2:261914005867:TatorNotifications"
                SpeechMarkTypes = new List<string>(),
                TaskId = taskId,
                TaskStatusReason = null,                // can be null
                TaskStatus = Amazon.Polly.TaskStatus.InProgress,        // or TaskStatus: { completed}
                TextType = TextType.Text,
                VoiceId = voiceId
            };

            speechSynthesisTaskDictionary.AddOrUpdate(taskId, synthesisTask, (_, synthesisTask) => synthesisTask);     // asserting that the key will never be reused/updated

            await Task.Factory.StartNew(async () =>
            {
                // after a delay, publish a Task status changed message
                await Task.Delay(10000);

                // set the task as complete ans publish a notification
                speechSynthesisTaskDictionary[taskId].TaskStatus = Amazon.Polly.TaskStatus.Completed;
                _ = await awsInterface.SNS.Publish(notificationsARN, $"The message body contains the taskId {taskId}").ConfigureAwait(true);
            });

            return await Task.FromResult(new AWSResult<SynthesisTask>(synthesisTask, null));
        }

        public async Task<AWSResult<SynthesisTask>> StartSpeechSynthesisTaskFromFile(string bucketName, string key, string arn, string filePath, Engine engine, string voiceId)
        {
            return await StartSpeechSynthesisTask(bucketName, key, arn, "", engine, voiceId);
        }

        public async Task<AWSResult<SynthesisTask>> GetSpeechSynthesisTask(string taskId)
        {
            await Task.Delay(1000);

            if (speechSynthesisTaskDictionary.ContainsKey(taskId))
            {
                var synthesisTask = speechSynthesisTaskDictionary[taskId];
                return await Task.FromResult(new AWSResult<SynthesisTask>(synthesisTask, null));
            }
            else
            {
                return await Task.FromResult(new AWSResult<SynthesisTask>(null, new AWSException("Mock GetSpeechSynthesisTask", $"The task {taskId} does not exist", new KeyNotFoundException("Key not found"))));
            }
        }

        public async Task<AWSResult<List<SynthesisTask>>> ListSpeechSynthesisTasks()
        {
            await Task.Delay(1000);

            var synthesisTasks = new List<SynthesisTask>(speechSynthesisTaskDictionary.Values);
            return await Task.FromResult(new AWSResult<List<SynthesisTask>>(synthesisTasks, null));
        }
    }
}
